SELECT attach_no, board_no, ori_name, re_name
FROM attach
WHERE board_no = #{boardNo}

SELECT attach_no, board_no, ori_name, re_name
FROM attach
WHERE attach_no = #{attachNo}

SELECT B.board_no, B.board_title, B.board_content, B.board_writer, DATE_FORMAT(B.reg_date, '%Y-%m-%d') AS reg_date, DATE_FORMAT(B.mod_date, '%Y-%m-%d') AS mod_date, M.member_id FROM board B LEFT JOIN member M ON B.board_writer = M.member_no
B.board_title LIKE CONCAT('%',#{keyword},'%') OR M.member_id LIKE CONCAT('%',#{keyword},'%')
LIMIT #{limitPageNo}, #{numPerPage}
